 FROM(
	SELECT 
	url,
	method,
	method_detail,
	in_param,
	JSON_UNQUOTE( JSON_EXTRACT(method_detail, CONCAT('$.','consumes[0]'))) AS consumes,
	GROUP_CONCAT(IFNULL(CONCAT(IF(in_param='query',CONCAT(`Element Type`,'=Dummy'),''),''),'') SEPARATOR "&") AS query_param,
	CONCAT("{",GROUP_CONCAT(IF(in_param='body',CONCAT('"',`Element Type`,'":""'),NULL)),"}") AS body
	-- ,`Element Type`
	-- ,`Data Type`
	-- ,Format
	-- ,Required
	-- ,Description
	-- ,Notes
	-- ,in_param
	FROM (
		SELECT 
		url
		,method
		,method_detail
		,JSON_EXTRACT(method_detail, CONCAT('$.','parameters')) AS parameters
		,JSON_EXTRACT(method_detail, CONCAT('$.','responses')) AS responses
		,JSON_EXTRACT(method_detail, CONCAT('$.','summary')) AS summary
		,p.name AS 'Element Type'
		,p.type AS 'Data Type'
		, '' AS 'Format'
		,p.required AS 'Required'
		,p.description AS 'Description' 
		,'' AS 'Notes'
		,p.in_param
		FROM(
			SELECT 
			  tbl.path_key AS url,
			  methods.method AS method,
			  JSON_UNQUOTE(JSON_EXTRACT(tbl.path_value, CONCAT('$.', methods.method))) AS method_detail
			FROM (
			  SELECT 
				jt.path_key,
				JSON_EXTRACT(@data, CONCAT('$.paths."', jt.path_key, '"')) AS path_value
			  FROM JSON_TABLE(
				JSON_KEYS(@data, '$.paths'),
				'$[*]' COLUMNS (
				  path_key VARCHAR(255) PATH '$'
				)
			  ) AS jt
			  -- where jt.path_key IN('/dh/list','/dh/add')
			) AS tbl
			JOIN JSON_TABLE(
			  JSON_KEYS(tbl.path_value),
			  '$[*]' COLUMNS (
				method VARCHAR(20) PATH '$'
			  )
			) AS methods
		)tbl
		LEFT JOIN JSON_TABLE(
		  JSON_EXTRACT(tbl.method_detail, '$.parameters'),
		  '$[*]' COLUMNS (
			NAME VARCHAR(50) PATH '$.name',
			in_param VARCHAR(20) PATH '$.in',
			required BOOLEAN PATH '$.required',
			TYPE VARCHAR(20) PATH '$.type',
			FORMAT VARCHAR(20) PATH '$.format',
			description VARCHAR(255) PATH '$.description'
		  )
		) p ON(TRUE)
	)tbl
	GROUP BY tbl.url
)final;